import { Response, Request } from 'express';
import { IMatchesController } from '../../interfaces/IMatches';
import MatchesService from '../services/Matches.service';

export default class MatchesController implements IMatchesController {
  constructor(private _service: MatchesService) {}

  async findAll(req: Request, res: Response): Promise<Response> {
    const { inProgress } = req.query;
    if (inProgress === 'true') {
      const matches = await this._service.findAllInProgress(true);
      return res.status(200).json(matches);
    }
    if (inProgress === 'false') {
      const matches = await this._service.findAllInProgress(false);
      return res.status(200).json(matches);
    }
    const matches = await this._service.findAll();
    return res.status(200).json(matches);
  }

  async createMatches(req: Request, res: Response): Promise<Response> {
    const result = req.body;
    const DATA = await this._service.createMatches(result);
    return res.status(201).json(DATA);
  }

  async changeInProgress(req: Request, res: Response): Promise<Response> {
    const ID = req.params.id;
    await this._service.changeInProgress(Number(ID));
    return res.status(200).json({ message: 'Finished' });
  }

  async changeResults(req: Request, res: Response): Promise<Response> {
    const ID = req.params.id;
    const { homeTeamGoals, awayTeamGoals } = req.body;
    await this._service.changeResults(
      Number(ID),
      Number(homeTeamGoals),
      Number(awayTeamGoals),
    );
    return res.status(200).json({ message: 'deu' });
  }
}

import { Request, Response, NextFunction } from 'express';
import TeamsService from '../database/services/Teams.service';
import JsonWebToken from './jwt';

const teamService = new TeamsService();
const DATAJWT = new JsonWebToken();

const verifyFields = (req: Request, res: Response, next: NextFunction) => {
  const { homeTeamId, awayTeamId } = req.body;
  if (homeTeamId === awayTeamId) {
    return res.status(422).json({
      message: 'It is not possible to create a match with two equal teams',
    });
  }
  return next();
};

const teamsExists = async (req: Request, res: Response, next: NextFunction) => {
  const { homeTeamId, awayTeamId } = req.body;
  const teamHome = await teamService.findById(Number(homeTeamId));
  const teamAway = await teamService.findById(Number(awayTeamId));
  if (!teamHome || !teamAway) {
    return res.status(404).json({ message: 'There is no team with such id!' });
  }
  return next();
};

const tokenIsValid = (req: Request, res: Response, next: NextFunction) => {
  /* console.log('oi'); */
  const token = req.headers.authorization;
  if (!token) {
    return res.status(401).json({ message: 'Token must be a valid token' });
  }
  try {
    const resultToken = DATAJWT.decode(token);
    if (!resultToken) {
      throw new Error('err');
    }
    req.body.user = resultToken;
    return next();
  } catch (err) {
    return res.status(401).json({ message: 'Token must be a valid token' });
  }
};
export { verifyFields, teamsExists, tokenIsValid };

import * as JWT from 'jsonwebtoken';
import { IUserLogin } from '../interfaces/IUser';

export default class JsonWebToken {
  public _secretKey: JWT.Secret = process.env.JWT_SECRET || 'jwt_secret';
  public _jwtConfig: JWT.SignOptions = { expiresIn: '15d', algorithm: 'HS256' };

  public generateToken(payload: IUserLogin) {
    const NewToken = JWT.sign({ ...payload }, this._secretKey, this._jwtConfig);
    return NewToken;
  }

  public decode(token: string) {
    const tokenIsValid = JWT.verify(token, this._secretKey);
    return tokenIsValid;
  }
}

